import React, { useEffect, useRef } from 'react';
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const severityChartRef = useRef(null);
  const priorityChartRef = useRef(null);

  useEffect(() => {
    // Function to destroy Chart instances
    const destroyCharts = () => {
      // Copy the current ref values to variables inside the effect
      const severityChartInstance = severityChartRef.current;
      const priorityChartInstance = priorityChartRef.current;

      if (severityChartInstance && severityChartInstance.chartInstance) {
        severityChartInstance.chartInstance.destroy();
      }
      if (priorityChartInstance && priorityChartInstance.chartInstance) {
        priorityChartInstance.chartInstance.destroy();
      }
    };

    // Destroy existing charts when the component unmounts
    return () => {
      destroyCharts();
    };
  }, []); // Add any dependencies here if needed

  // Rest of your code for creating and updating charts
  // ...

  return (
    <div>
      <div>
        <h2>Severity Chart</h2>
        <Pie data={severityChartData} ref={severityChartRef} />
      </div>
      <div>
        <h2>Priority Chart</h2>
        <Pie data={priorityChartData} ref={priorityChartRef} />
      </div>
    </div>
  );
};

export default Charts;
