import React, { useState, useEffect } from 'react'
import { IssueForm } from './IssueForm'
import { Issue } from './Issue'
import { v4 as uuidv4 } from 'uuid'
import { EditIssueForm } from './EditIssueForm'
import Sorter from './Sorter'
import SearchBar from './SearchBar'


//TODO: Pie graph, auth, prioritize, better icons, logo, search as component
  //<div className='delete-button' onClick={() => { if (window.confirm('Are you sure you wish to delete this item?')) this.onCancel(item) } } />


const IssueWrapper = () => {
    const[issues,setIssues] = useState(()=>{
        const tempJson = localStorage.getItem("ISSUES")
        if(tempJson==null) return []

        return JSON.parse(tempJson)
    })

    useEffect(()=>{
        localStorage.setItem("ISSUES", JSON.stringify(issues))
    },[issues])

    //const [query, setQuery] = useState("")
  
    const addIssue = (
            issue, 
            line, 
            component, 
            error, 
            priority, 
            severity, 
            statusKey, 
            assignee) => {

        const datePost = new Date().toLocaleString()

        setIssues([
            ...issues, 
            {
                id: uuidv4(),
                issue: issue,
                completed: false,
                isEditing: false,
                line: line,
                component: component,
                date: datePost,
                error: error,
                severity: severity,
                priority: priority,
                statusKey: statusKey,
                assignee: assignee
            }
        ])
    }

    const toggleComplete = id => {
        setIssues(
          issues.map(issue =>
            issue.id === id ? { ...issue, completed: !issue.completed } : issue
          )
        );
    }

    const deleteIssue = id => {
        setIssues(issues.filter(value=> value.id !== id))
    }

    const editIssue = id =>{
        setIssues(issues.map(issue=> issue.id === id ?
            {...issue, isEditing: !issue.isEditing}: issue))
    }

    const handleCancel = id => {
        setIssues((prevIssue) => {
          const newIssue = [...prevIssue];
          const originalIssue = newIssue[id];
          newIssue[id] = { ...originalIssue, isEditing: false };
          return newIssue;
        });
      };
    
    
    const editTask = (
        idOriginal,
        issue, 
        line,
        component, 
        error, 
        priority, 
        severity,
        statusKey,
        assignee) => {

            const datePost = new Date().toLocaleString()

            setIssues(issues.map(issueUnit => issueUnit.id === idOriginal ?
                {        
                    id: idOriginal,            
                    issue: issue,
                    completed: false,
                    isEditing: false,
                    line: line,
                    component: component,
                    date: datePost,
                    error: error,
                    severity: severity,
                    priority: priority,
                    statusKey: statusKey,
                    assignee: assignee,
                } : issueUnit
            ))
        }
    
    const keys = [
        "issue", 
        "line", 
        "component", 
        "error", 
        "priority", 
        "severity", 
        "statusKey",
        "assignee"
    ]

   const handleSort = (order, key) => {
        if(order === "ASC"){
            const sorted = [...issues].sort((a,b) =>
                a[key] > b[key] ? 1 : -1
            )
            setIssues(sorted)
        }
        if(order === "DSC"){
            const sorted = [...issues].sort((a,b) =>
                a[key] < b[key] ? 1 : -1
            )
            setIssues(sorted)
        }
   }


  return (
    <div className='TodoWrapper'>
        <h1>Bug Ranger</h1>
        <IssueForm addIssue={addIssue}/>
        {/*<input className='search-bar'
            placeholder='Search your issue'
            onChange={e=>setQuery(e.target.value)}
            ></input>*/}
        <Sorter onSort={handleSort}/>
        <SearchBar queried={query}/>
        {issues.filter(issue=>(
            query !== ""?
            keys.some(key=>issue[key].includes(query)) :
            issues
        ))
        .map((issue,index)=>(
            issue.isEditing ? (
                <EditIssueForm editBug={editTask} 
                issuePrev={issue}
                idIssue={issue.id}
                onCancel={() => handleCancel(index)}/>
            ) : (
                <Issue issue={issue}
                key={index}
                deleteIssue={deleteIssue}
                editIssue={editIssue}
                toggleComplete={toggleComplete}/>
            )
        ))}
    </div>
  )
}

export default IssueWrapper
