import React, { useState } from 'react'

export const TodoForm = ({addTodo}) => {
    
   /* const[input, setInput] = useState({
      task: "",
      line: ""
    })*/
 

    const [value, setValue] = useState({
      task: "",
      line: "",
      error: "",

    })

    const handleChange = e =>{
        
      const { name, value } = e.target           

        setValue((prevVal) => ({
          ...prevVal,
          [name]: value
        }))
         
        console.log({[name]:value})
        console.log(value.task)
        console.log(value.line)
    }

    const handleSubmit = e =>{
        e.preventDefault()

        const errorString = error.toString()
        const { task, line, error } = value

        if(value){
            addTodo(task, line, errorString)
            console.log("submit")
            setValue("")
        }

        setValue("")
    }
 
  return (
    <form className='TodoForm' onSubmit={handleSubmit}>
        <input type='text'
        name='task'
        className='todo-input'
        value={value.task}
        placeholder='Bug' 
        //onChange={e=> setValue(e.target.value)}
        onChange={handleChange}

        />
        <input type='text'
        name='line'
        className='todo-input'
        value={value.line}
        placeholder='Line' 
        //onChange={e=> setValue(e.target.value)}
        onChange={handleChange}
        />     
        <select 
          name="error"
          onSelect={handleChange}
          value={value.error}
          
          >
            <option value="syntax">Syntax Error</option>
            <option value="logic">Logic Error</option>
            <option value="type-error">Type Error</option>
            <option value="rt">Runtime Error</option>
            <option value="rt">Null Pointer Error</option>
            <option value="other">Other</option>
        </select>       
        <button type='submit' className='todo-btn'>Add Task</button>
    </form>
  )
}
