import React, { useEffect, useRef, useState } from 'react';
import 'chart.js/auto'; // Import 'chart.js/auto' to ensure proper initialization
import { Chart as ChartJS } from "chart.js/auto";
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const [severityCounts, setSeverityCounts] = useState(
    // {
    //   labels: Object.values(issues.severity),
    //   datasets: [
    //     {
    //       data: issues.map(),
    //       backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
    //     },
    //   ],
    // }
    );
    // labels: UserData.map((data) => data.year),
    // datasets: [
    //   {
    //     label: "Users Gained",
    //     data: UserData.map((data) => data.userGain)
    //   },
    // ],
  // const chartRef = useRef(null); // Reference to the chart instance
  // let countKey = Object.keys(counts)
  // let countValue = Object.keys(counts)

  useEffect(() => {
    // Function to calculate the severity counts
    const calculateSeverityCounts = (issues) => {
      const counts = {};
      issues.forEach((issue) => {
        const severity = issue.severity;
        if (severity) {
          if (counts[severity]) {
            counts[severity]++;
          } else {
            counts[severity] = 1;
          }
        }
      });
      return counts;
    };
    
  }, [issues]);
  
  const counts = calculateSeverityCounts(issues);
  setSeverityCounts(counts);
  console.log(counts);
  console.log(severityCounts)
  // console.log(countKey)


  return (
    <div>
      <div>
        <h2>Charts</h2>
        {/* <Pie data={data}/> */}
      </div>
    </div>
  );
};

export default Charts;
