import React, { useEffect, useRef, useState } from 'react';
import 'chart.js/auto'; // Import 'chart.js/auto' to ensure proper initialization
import { Chart as ChartJS } from "chart.js/auto";
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const [severityCounts, setSeverityCounts] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
      },
    ],
  })

  const calculateSeverityCounts = (issues) => {
    const counts = {};
    issues.forEach((issue) => {
      const severity = issue.severity;
      if (severity) {
        if (counts[severity]) {
          counts[severity]++;
        } else {
          counts[severity] = 1;
        }
      }
    });
    return counts;
  };
  
  const counts = calculateSeverityCounts(issues);
  console.log(counts);
  console.log(severityCounts)
  
  useEffect(() => {
    
    setSeverityCounts({
      labels: Object.keys(counts),
      datasets: [
        {
          data: Object.values(counts),
          backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        },
      ],
    });
  }, [issues, counts]);

  return (
    <div>
      <div>
        <h2>Charts</h2>
        <Pie data={severityCounts.datasets[0].data}/>
      </div>
    </div>
  );
};

export default Charts;
