import React, { useState } from 'react'
import { Link, useMatch, useResolvedPath } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChartSimple } from '@fortawesome/free-solid-svg-icons'
import { faFlag } from '@fortawesome/free-solid-svg-icons'


const issueIcon = <FontAwesomeIcon icon={faFlag}  />
const chartsIcon = <FontAwesomeIcon icon={faChartSimple} />


const NavBar = () => {  
  return (
    <nav className='nav'>
        <ul>
        <CustomLink to="/" >{issueIcon}</CustomLink>
        <CustomLink to="/charts" >{chartsIcon}</CustomLink>          
        </ul>        
    </nav>
  )
}

function CustomLink({ to, children, ...props }) {
  const resolvedPath = useResolvedPath(to)
  const isActive = useMatch({ path: resolvedPath.pathname, end: true })
  
  const [active, setActive] = useState("fa-flag")

  const handleClick = () => {
    switch(to){
      case "/":
        setActive("fa-flag")
      break
      case "/charts":
        setActive("fa-charts-simple")
      break
    }  
    console.log(to)
  }

  return (
    <li>
      <Link to={to} {...props}  
            className={`${active}${isActive ? "active" : ""}`} 
             onClick={handleClick}>
        {children}
      </Link>
    </li>
  )
}

export default NavBar
