import React from 'react'
import { Link } from 'react-router-dom'

const NavBar = () => {
  return (
    <nav className='nav'>
        <ul>
        <CustomLink to="/" className='issues-link'>Issues</CustomLink>
        <CustomLink to="/charts" className='charts-link'>Charts</CustomLink>          
        </ul>        
    </nav>
  )
}

function CustomLink ({to,children,...props}){
    const path = window.location.pathname
    return (
        <li className={path === to? "active" : ""}>
            <Link to={to}{...props}>
                {children}
            </Link>            
        </li>
    )
}

export default NavBar
