import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChartSimple } from '@fortawesome/free-solid-svg-icons'
import { faFlag } from '@fortawesome/free-solid-svg-icons'


const issueIcon = <FontAwesomeIcon icon={faFlag}  />
const chartsIcon = <FontAwesomeIcon icon={faChartSimple} size="2x" />


const NavBar = () => {
  
  const [active, setActive] = useState("issues-link")
  
  const handleClick = e => {
    setActive(e.target)
  }
  
  return (
    <nav className='nav'>
        <ul>
        <Link to="/" className='issues-link'>{issueIcon}</Link>
        <Link to="/charts" className='charts-link'>{chartsIcon}</Link>          
        </ul>        
    </nav>
  )
}



export default NavBar
