import React, { useState } from 'react'
import { Link, useMatch, useResolvedPath } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChartSimple } from '@fortawesome/free-solid-svg-icons'
import { faFlag } from '@fortawesome/free-solid-svg-icons'


const issueIcon = <FontAwesomeIcon icon={faFlag}  />
const chartsIcon = <FontAwesomeIcon icon={faChartSimple} size="2x" />


const NavBar = () => {
  
  const [active, setActive] = useState("issues")

  const path = window.location.pathname
  
  const handleClick = () => {
    switch(path){
      case "/":
        setActive("issues")
      break
      case "/charts":
        setActive("charts")
      break
    }

    console.log(path)
  }
  
  return (
    <nav className='nav'>
        <ul>
        <CustomLink to="/">{issueIcon}</CustomLink>
        <Link to="/charts" className={`charts-link' ${active==="charts"? "active" : "inactive"}`} 
              onClick={()=>handleClick()}>{chartsIcon}</Link>          
        </ul>        
    </nav>
  )
}

function CustomLink({ to, children, ...props }) {
  const resolvedPath = useResolvedPath(to)
  const isActive = useMatch({ path: resolvedPath.pathname, end: true })

  return (
    <li className={isActive ? "active" : ""}>
      <Link to={to} {...props}>
        {children}
      </Link>
    </li>
  )
}

export default NavBar
