import React, { useEffect, useRef, useState } from 'react';
import 'chart.js';
import { Pie } from 'react-chartjs-2';

const Charts = ({ issuesData }) => {
  
  const [severityCounts, setSeverityCounts] = useState({});

  // Function to calculate the severity counts
  const calculateSeverityCounts = (issues) => {
    const counts = {};
    
    issues.forEach((issue) => {
      const severity = issue.severity;
      if (severity) {
        if (counts[severity]) {
          counts[severity]++;
        } else {
          counts[severity] = 1;
        }
      }
    });

    return counts;
  };

  useEffect(() => {
    // Calculate the severity counts when issues prop changes
    const counts = calculateSeverityCounts(issues);
    setSeverityCounts(counts);
  }, [issues]);

  // Log the severity counts to the console
  useEffect(() => {
    console.log(severityCounts);
  }, [severityCounts]);

  
  return (
    <div>
      <div>
        <h2>Severity Chart</h2>
        {/* {value && <Pie data={value} ref={severityChartRef} />} */}

      </div>
    </div>
  );
};

export default Charts;
