import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChartSimple } from '@fortawesome/free-solid-svg-icons'
import { faFlag } from '@fortawesome/free-solid-svg-icons'


const issueIcon = <FontAwesomeIcon icon={faFlag}  />
const chartsIcon = <FontAwesomeIcon icon={faChartSimple} size="2x" />


const NavBar = () => {
  
  const [active, setActive] = useState("issues")
  
  const handleClick = () => {
    switch(window.location.pathname){
      case "/":
        setActive("issues")
      break
      case "/charts":
        setActive("charts")
      break
    }
  }
  
  return (
    <nav className='nav'>
        <ul>
        <Link to="/" className={`issues-link' ${active==="issues"? ".active" : ""}`} 
              onClick={()=>handleClick()}>{issueIcon}</Link>
        <Link to="/charts" className={`charts-link' ${active==="charts"? ".active" : ""}`} 
              onClick={()=>handleClick()}>{chartsIcon}</Link>          
        </ul>        
    </nav>
  )
}

export default NavBar
