import React, { useState } from 'react'

export const TodoForm = ({addTodo}) => {
    
    const [value, setValue] = useState({
      task: "",
      line: "",
      error: "",
      severity: "",
      priority: "",
      status: "",
      compo: "",
      assignee: "",
    })

    const [errors, setErrors] = useState({
      task: "",
      line: "",
      error: "",
      severity: "",
      priority: "",
      status: "",
      compo: "",
      assignee: "",
    })


    const handleChange = e =>{
        
      const { name, value } = e.target           

        setValue((prevVal) => ({
          ...prevVal,
          [name]: value
        }))
         
        console.log({[name]:value})
        console.log(value.error)
        
    }

    
    const handleSelect = (isValid) =>{
        
      isValid = false
      // Perform validation
      const { task, line, error, severity,
        priority, status, compo, assignee } = value;
        const validationErrors = {};
        console.log(validationErrors)
        
          if (task === '') {
            validationErrors.task = 'Please enter a description.';
          }

          if (line === '') {
            validationErrors.line = 'Please enter a line.';
          }

          if (compo === '') {
            validationErrors.compo = 'Please enter a component.';
          }

          console.log(validationErrors)


          // Update error state
          setErrors(validationErrors);

          console.log(validationErrors)


          // If there are errors, return without submitting
          if (Object.keys(validationErrors).length > 0) {
            return;
          }

          isValid = true
          // Validation successful, perform form submission actions
          console.log('Form submitted:', value);

          setValue({
            task: "",
            line: "",
            error: "",
            severity: "",
            priority: "",
            status: "",
            compo: "",
            assignee: "",
          })

          setErrors({
            task: "",
            line: "",
            error: "",
            severity: "",
            priority: "",
            status: "",
            compo: "",
            assignee: "",
          })

    }


    const handleSubmit = e =>{
      
        handleSelect(isValid)

        if(isValid)
        
        e.preventDefault()


        const { 
          task, 
          line,
          compo, 
          error, 
          priority, 
          severity,
          status,
          assignee } = value
      
        
        if(value){
            addTodo(task, line, 
              compo, error, priority, 
              severity, status, assignee)
            console.log("submit")
            setValue("")
        }
        setValue("")
    }
 
  return (
    <form className='TodoForm' onSubmit={handleSubmit}>
        <input type='text'
        name='task'
        className='todo-input'
        value={value.task}
        placeholder='Title (max. 120)' 
        onChange={handleChange}
        maxLength="126"
        />
        {errors.task && <p>{errors.task}</p>}

        <input type='number'
        name='line'
        className='todo-input'
        value={value.line}
        placeholder='Line' 
        onChange={handleChange}
        /> 

        <input type='text'
        name='compo'
        className='todo-input'
        value={value.compo}
        placeholder='Component' 
        onChange={handleChange}
        maxLength="50"
        />   

        <select
          name="error"
          value={value.error}          
          onChange={handleChange}
          placeholder='Type of error'
          >
            <option value="Syntax Error" >Syntax Error</option>
            <option value="Logic Error">Logic Error</option>
            <option value="Type Error">Type Error</option>
            <option value="Runtime Error">Runtime Error</option>
            <option value="Null Pointer Error">Null Pointer Error</option>
            <option value="Other" >Other</option>
        </select>

        <select 
          name="severity"
          value={value.severity}          
          onChange={handleChange}
          placeholder='Severity'
          >
            <option value="Minor">Minor</option>
            <option value="Major">Major</option>
            <option value="Critical">Critical</option>            
        </select>  

        <select 
          name="priority"
          value={value.priority}          
          onChange={handleChange}
          placeholder='Priority'
          >
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>            
        </select>  

        <select 
          name="status"
          value={value.status}          
          onChange={handleChange}
          >
            <option value="Open">Open</option>
            <option value="Assigned">Assigned</option>
            <option value="In Progress">In Progress</option>
            <option value="Closed">Closed</option>            
        </select> 

        <select 
          name="assignee"
          value={value.assignee}          
          onChange={handleChange}
          >
            <option value="John">John</option>
            <option value="James">James</option>
            <option value="Jack">Jack</option>
        </select>   

        <button type='submit' className='todo-btn'>Add Task</button>
    </form>
  )
}
