import React, { useState, useEffect } from 'react'
import { IssueForm } from './IssueForm'
import { Issue } from './Issue'
import { v4 as uuidv4 } from 'uuid'
import { EditTodoForm } from './EditTodoFrom'

uuidv4()


const IssueWrapper = () => {
    const[issues,setIssues] = useState(()=>{
        const tempJson = localStorage.getItem("ISSUES")
        if(tempJson==null) return []

        return JSON.parse(tempJson)
    })

    useEffect(()=>{
        localStorage.setItem("ISSUES", JSON.stringify(issues))
    },[issues])

  
    const addIssue = (
            issue, 
            line, 
            compo, 
            error, 
            priority, 
            severity, 
            status, 
            assignee) => {

        const datePost = new Date().toLocaleString()
        console.log(datePost)

        setIssues([
            ...issues, 
            {
                id: uuidv4(),
                issue: issue,
                completed: false,
                isEditing: false,
                line: line,
                compo: compo,
                date: datePost,
                error: error,
                severity: severity,
                priority: priority,
                status: status,
                assignee: assignee
            }
        ])
    }

    const mapId = id => {
        let array = Object.entries(issues)
        console.log(issues)
        console.log(array)
    }

    mapId()

    const toggleComplete = id => {
        setIssues(
          issues.map(issue =>
            issue.id === id ? { ...issue, completed: !issue.completed } : issue
          )
        );
    }

    const deleteIssue = id => {
        setIssues(issues.filter(value=> value.id !== id))
    }

    const editIssue = id =>{
        setIssues(issues.map(issue=> issue.id === id ?
            {...issue, isEditing: !issue.isEditing}: issue))
    }
    
    /*const editTask = (task, id) =>{
        setIssues(issues.map(issue => issue.id === id ?
            {...issue, task, isEditing: !issue.isEditing} : issue
        ))
    }*/

    const editTask = (
        idOriginal,
        issue, 
        line, 
        compo, 
        error, 
        priority, 
        severity, 
        status, 
        assignee) => {

            console.log(idOriginal)

            const datePost = new Date().toLocaleString()

            setIssues(issues.map(issueMap => issueMap.id === idOriginal ?
                {
                    id: idOriginal,                                     
                    issue: issue,
                    completed: false,
                    isEditing: false,
                    line: line,
                    compo: compo,
                    date: datePost,
                    error: error,
                    severity: severity,
                    priority: priority,
                    status: status,
                    assignee: assignee
                }  : issue
            ))
        }
    

    
  return (
    <div className='TodoWrapper'>
        <h1>Bug Ranger</h1>
        <IssueForm addIssue={addIssue}/>
        {issues.map((issue,index)=>(
            issue.isEditing ? (
                <EditTodoForm editBug={editTask} 
                issuePrev={issue}
                idOriginal={issue.id}/>
            ) : (
                <Issue issue={issue}
                key={index}
                deleteIssue={deleteIssue}
                editIssue={editIssue}
                toggleComplete={toggleComplete}/>
            )
        ))}
    </div>
  )
}

export default IssueWrapper
