import React, { useEffect, useRef, useState } from 'react';
import 'chart.js/auto'; // Import 'chart.js/auto' to ensure proper initialization

import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const [severityCounts, setSeverityCounts] = useState({});
  const chartRef = useRef(null); // Reference to the chart instance

  useEffect(() => {
    // Function to calculate the severity counts
    const calculateSeverityCounts = (issues) => {
      const counts = {};
      issues.forEach((issue) => {
        const severity = issue.severity;
        if (severity) {
          if (counts[severity]) {
            counts[severity]++;
          } else {
            counts[severity] = 1;
          }
        }
      });
      return counts;
    };
    const counts = calculateSeverityCounts(issues);
    setSeverityCounts(counts);
    console.log(counts);

    const createChart = () => {
      const ctx = document.getElementById('severityChart');
      if (ctx) {
        const data = {
          labels: Object.keys(counts),
          datasets: [
            {
              data: Object.values(counts),
              backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
            },
          ],
        };

        new document.Charts(ctx, {
          type: 'pie',
          data: data,
        });
      }
    };

    createChart();
  }, [issues]);

  return (
    <div>
      <div>
        <h2>Charts</h2>
        <canvas id="severityChart" width="400" height="400"></canvas>
      </div>
    </div>
  );
};

export default Charts;
