import React, { useEffect, useRef } from 'react';
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const severityChartRef = useRef(null);
  const priorityChartRef = useRef(null);

  useEffect(() => {
    // Destroy the previous chart instances when the component unmounts
    return () => {
      if (severityChartRef.current && severityChartRef.current.chartInstance) {
        severityChartRef.current.chartInstance.destroy();
      }
      if (priorityChartRef.current && priorityChartRef.current.chartInstance) {
        priorityChartRef.current.chartInstance.destroy();
      }
    };
  }, []);

  // Create objects to count the repetitions of severity and priority
  const severityCounts = {};
  const priorityCounts = {};

  issues.forEach((issue) => {
    if (issue.severity) {
      if (severityCounts[issue.severity]) {
        severityCounts[issue.severity]++;
      } else {
        severityCounts[issue.severity] = 1;
      }
    }

    if (issue.priority) {
      if (priorityCounts[issue.priority]) {
        priorityCounts[issue.priority]++;
      } else {
        priorityCounts[issue.priority] = 1;
      }
    }
  });

  const severityLabels = Object.keys(severityCounts);
  const severityData = Object.values(severityCounts);

  const priorityLabels = Object.keys(priorityCounts);
  const priorityData = Object.values(priorityCounts);

  // Create data for the Severity Pie Chart
  const severityChartData = {
    labels: severityLabels,
    datasets: [
      {
        data: severityData,
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  // Create data for the Priority Pie Chart
  const priorityChartData = {
    labels: priorityLabels,
    datasets: [
      {
        data: priorityData,
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  return (
    <div>
      <div>
        <h2>Severity Chart</h2>
        <Pie data={severityChartData} ref={severityChartRef} />
      </div>
      <div>
        <h2>Priority Chart</h2>
        <Pie data={priorityChartData} ref={priorityChartRef} />
      </div>
    </div>
  );
};

export default Charts;
