import React, { useEffect, useRef, useState } from 'react';
import 'chart.js';
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const [severityChartData, setSeverityChartData] = useState(null);
  const severityChartRef = useRef(null);

  useEffect(() => {
    // Store severityChartRef.current in a variable
    const severityChartInstance = severityChartRef.current;
  
    // Destroy the previous chart instance when the component unmounts
    return () => {
      if (severityChartInstance && severityChartInstance.chartInstance) {
        severityChartInstance.chartInstance.destroy();
      }
    };
  }, []);

  useEffect(() => {
    // Calculate the counts of each severity level
    const severityCounts = {
      Minor: 0,
      Major: 0,
      Critical: 0,
    };

    issues.forEach((issue) => {
      if (issue.severity && severityCounts.hasOwnProperty(issue.severity)) {
        severityCounts[issue.severity]++;
      }
    });

    // Create data for the Severity Pie Chart
    const data = {
      labels: Object.keys(severityCounts),
      datasets: [
        {
          data: Object.values(severityCounts),
          backgroundColor: ['#FFCE56', '#36A2EB', '#FF6384'],
          hoverBackgroundColor: ['#FFCE56', '#36A2EB', '#FF6384'],
        },
      ],
    };

    setSeverityChartData(data);
  }, [issues]);

  return (
    <div>
      <div>
        <h2>Severity Chart</h2>
        {severityChartData && <Pie data={severityChartData} ref={severityChartRef} />}
      </div>
    </div>
  );
};

export default Charts;
