import React, { useState } from 'react'

export const TodoForm = ({addTodo}) => {
    
   /* const[input, setInput] = useState({
      task: "",
      line: ""
    })*/
 

    const [value, setValue] = useState({
      task: "",
      line: "",
      error: "",
      severity: "",
      priority: ""
    })

    const handleChange = e =>{
        
      const { name, value } = e.target           

        setValue((prevVal) => ({
          ...prevVal,
          [name]: value
        }))
         
        console.log({[name]:value})
        console.log(value.task)
        console.log(value.line)
        console.log(value.error)
    }

    const handleSubmit = e =>{
        e.preventDefault()

        const { task, line, error } = value
      
        
        if(value){
            addTodo(task, line, error)
            console.log("submit")
            setValue("")
        }

        setValue("")
    }
 
  return (
    <form className='TodoForm' onSubmit={handleSubmit}>
        <input type='text'
        name='task'
        className='todo-input'
        value={value.task}
        placeholder='Bug' 
        //onChange={e=> setValue(e.target.value)}
        onChange={handleChange}
        />

        <input type='text'
        name='line'
        className='todo-input'
        value={value.line}
        placeholder='Line' 
        //onChange={e=> setValue(e.target.value)}
        onChange={handleChange}
        />  

        <select 
          name="error"
          value={value.error}          
          onChange={handleChange}
          >
            <option value="Syntax Error">Syntax Error</option>
            <option value="Logic Error">Logic Error</option>
            <option value="Type Error">Type Error</option>
            <option value="Runtime Error">Runtime Error</option>
            <option value="Null Pointer Error">Null Pointer Error</option>
            <option value="Other">Other</option>
        </select>

        <select 
          name="severity"
          value={value.severity}          
          onChange={handleChange}
          >
            <option value="Minor">Minor</option>
            <option value="Major">Major</option>
            <option value="Critical">Critical</option>            
        </select>  

        <select 
          name="priority"
          value={value.priority}          
          onChange={handleChange}
          >
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>            
        </select>  


        <button type='submit' className='todo-btn'>Add Task</button>
    </form>
  )
}
