import React from 'react';
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  // Create an object to count the repetitions of each property
  const propertyCounts = {};

  issues.forEach((issue) => {
    // Assuming each issue has a 'property' field that you want to count
    const property = issue.property;

    if (propertyCounts[property]) {
      propertyCounts[property]++;
    } else {
      propertyCounts[property] = 1;
    }
  });

  const labels = Object.keys(propertyCounts);
  const counts = Object.values(propertyCounts);

  // Create data for the Pie Chart
  const chartData = {
    labels: labels,
    datasets: [
      {
        data: counts,
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  return (
    <div>
      <h2>Chart</h2>
      <Pie data={chartData} />
    </div>
  );
};

export default Charts;
