import React, { useState } from 'react'

export const TodoForm = ({addTodo}) => {
    
    const [value, setValue] = useState({
      task: "",
      line: "",
      error: "",
      severity: "",
      priority: "",
      status: "",
      compo: "",
      assignee: ""
    })

    const [errors, setErrors] = useState({
      message: "",
      isValid: true,
    })

    console.log(value)

    const handleChange = e =>{
        
      const { name, value } = e.target           

        setValue((prevVal) => ({
          ...prevVal,
          [name]: value
        }))
         
        console.log({[name]:value})
        console.log(value.error)
        
    }

    
    const handleSelect = () =>{
        
      // Perform validation
      const { task, line, error, severity,
        priority, status, compo, assignee } = value
  
        if (task === "") {
            setErrors(errors.isValid = !errors.isValid, 
              errors.message = `Please enter a description`)
        }

        if(line === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please enter the line`)
        }

        if (compo === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please enter the component`)
        }

        if (error === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please select the type of error`)
        }

        if (priority === "") {
            setErrors(errors.isValid = false, 
              errors.message = `Please select the priority`)
        }

        if(severity === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please select the severity of the problem`)
        }

        if (status === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please select the status`)
        }

        if (assignee === '') {
          setErrors(errors.isValid = false, 
            errors.message = `Please select the assignee responsible`)
        }


          // If there are errors, return without submitting
        if (errors.isValid === false) {
            return;
        }

          // Validation successful, perform form submission actions
        console.log('Form submitted:', value);

        setValue({
            task: "",
            line: "",
            error: "",
            severity: "",
            priority: "",
            status: "",
            compo: "",
            assignee: "",
        })
    }


    const handleSubmit = e =>{
        
      e.preventDefault()
      
        handleSelect()

        if(errors.isValid === false) return

        setErrors({
          message: "",
          isValid: true
        })

        const { 
          task, 
          line,
          compo, 
          error, 
          priority, 
          severity,
          status,
          assignee } = value
      
        
        if(value){
            addTodo(task, line, 
              compo, error, priority, 
              severity, status, assignee)
            console.log("submit")
            setValue({
              task: "",
              line: "",
              error: "",
              severity: "",
              priority: "",
              status: "",
              compo: "",
              assignee: "",
            })   
        }
        setValue({
          task: "",
          line: "",
          error: "",
          severity: "",
          priority: "",
          status: "",
          compo: "",
          assignee: "",
        })            
    }
 
  return (
    <form className='TodoForm'
          onSubmit={handleSubmit}>
        <input type='text'
        name='task'
        className='todo-input'
        value={value.task}
        placeholder='Title (max. 120)' 
        onChange={handleChange}
        maxLength="126"
        />

        <input type='number'
        name='line'
        className='todo-input'
        value={value.line}
        placeholder='Line' 
        onChange={handleChange}
        /> 

        <input type='text'
        name='compo'
        className='todo-input'
        value={value.compo}
        placeholder='Component' 
        onChange={handleChange}
        maxLength="50"
        />   

        <select
          name="error"
          value={value.error}          
          onChange={handleChange}
          defaultValue={value.error}
          >
            <option value="" disabled hidden >Type of Error</option>
            <option value="Syntax Error" >Syntax Error</option>
            <option value="Logic Error">Logic Error</option>
            <option value="Type Error">Type Error</option>
            <option value="Runtime Error">Runtime Error</option>
            <option value="Null Pointer Error">Null Pointer Error</option>
            <option value="Other" >Other</option>
        </select>

        <select 
          name="severity"
          value={value.severity}          
          onChange={handleChange}
          placeholder='Severity'
          >            
            <option value="" disabled hidden >Severity</option>
            <option value="Minor">Minor</option>
            <option value="Major">Major</option>
            <option value="Critical">Critical</option>            
        </select>  

        <select 
          name="priority"
          value={value.priority}          
          onChange={handleChange}
          placeholder='Priority'
          >
            <option value="" disabled hidden >Priority</option>
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>            
        </select>  

        <select 
          name="status"
          value={value.status}          
          onChange={handleChange}
          >
            <option value="" disabled hidden >Status</option>
            <option value="Open">Open</option>
            <option value="Assigned">Assigned</option>
            <option value="In Progress">In Progress</option>
            <option value="Closed">Closed</option>            
        </select> 

        <select 
          name="assignee"
          value={value.assignee}          
          onChange={handleChange}
          >
            <option value="" disabled hidden >Assignee</option>
            <option value="John">John</option>
            <option value="James">James</option>
            <option value="Jack">Jack</option>
        </select>   

        <button type='submit' className='todo-btn'>Add Task</button>
    </form>
  )
}
