import React, { useEffect, useRef, useState } from 'react';
import 'chart.js/auto'; // Import 'chart.js/auto' to ensure proper initialization
import { Chart as ChartJS } from "chart.js/auto";
import { Pie } from 'react-chartjs-2';

const Charts = ({ issues }) => {
  const [severityData, setSeverityData] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ['green', 'red', 'yellow'],
      },
    ],
  })

  const calculateSeverity = (issues) => {
    const severityCounts = {};
    issues.forEach((issue) => {
      const severity = issue.severity;
      if (severity) {
        if (severityCounts[severity]) {
          severityCounts[severity]++;
        } else {
          severityCounts[severity] = 1;
        }
      }
    });
    return severityCounts;
  };
  
  const severityCounts = calculateSeverity(issues);
  console.log(severityCounts);
  console.log(severityData)
  
  useEffect(() => {
    
    setSeverityData({
      labels: Object.keys(severityCounts),
      datasets: [
        {
          data: Object.values(severityCounts),
          color: ['red', 'blue', 'green'],
          backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        },
      ],
    });
  }, [issues]);

  return (
    <div>
      <div>
        <h1>Charts</h1>
        <h2>Severity</h2>

        <Pie data={severityData}/>
      </div>
    </div>
  );
};

export default Charts;
